mapper.update:
Update perfiles
set estado = 0
where id = #{codigoPerfil}

mapper.select:
select A.id, A.nombre, A.descripcion, A.estado, A.fecha_creacion, A.creado_por, A.fecha_modifcacion, A.modifcado_por, A.eliminado_por from perfiles A
CONCAT(A.nombre) LIKE CONCAT(#{search},'%')
AND A.estado = #{habilitado}
ORDER BY ${sort} ${order}
